const divEscudos=document.getElementById('divEscudos');const imgPath='../../images/carta-bazi-imagenes/escudo.jpg';const aVolver=document.querySelector('a');aVolver.addEventListener('click',goBack);populateImages();document.querySelector('html').style.visibility='visible';scrollDown();exaltateSelectedAnimal();function scrollDown(){if(sessionStorage.getItem('pressedAnimal')){let animal=sessionStorage.getItem('pressedAnimal');window.location.hash='p'+animal;}}function exaltateSelectedAnimal(){if(sessionStorage.getItem('pressedAnimal')){let animal=sessionStorage.getItem('pressedAnimal');let pAnimal=document.getElementById('p'+animal);pAnimal.style.transition="background 2.0s linear 0s";pAnimal.style.background='#9DCEFF'}}function populateImages(){const maxWidthSmartphone=990;const maxWidthTablet=1099;const maxWidthNotebook=1366;const imgHeightSmartPhone=220;const imgHeightTablet=290;const imgHeightNotebook=400;const imgHeightMonitor=550;const marginTopSmartPhone=11;const marginBottomTablet=10;const marginBottomNotebook=15;const marginBottomMonitor=20;let nPictures;let scrollHeight=Math.max(document.body.scrollHeight,document.documentElement.scrollHeight,document.body.offsetHeight,document.documentElement.offsetHeight,document.body.clientHeight,document.documentElement.clientHeight);if(screen.width<=maxWidthSmartphone){nPictures=parseInt((scrollHeight-marginTopSmartPhone)/imgHeightSmartPhone,10)-2}else if(screen.width<=maxWidthTablet){nPictures=parseInt((scrollHeight-marginBottomTablet)/(imgHeightTablet+marginBottomTablet),10)}else if(screen.width<=maxWidthNotebook){nPictures=parseInt((scrollHeight-marginBottomNotebook)/(imgHeightNotebook+marginBottomNotebook),10);}else{nPictures=parseInt((scrollHeight-marginBottomMonitor)/(imgHeightMonitor+marginBottomMonitor),10);}for(let i=0;i<nPictures;i++){let img=document.createElement('img');img.setAttribute('src',imgPath);img.setAttribute('display','inline');divEscudos.appendChild(img)}}function goBack(){window.history.back()}